}

interface WriteOffOptions extends BaseMutationOptions {
  itemId: string;
  warehouseId: string;
  quantity: number;
}

interface DonationOptions extends BaseMutationOptions {
  itemId: string;
  warehouseId: string;
  quantity: number;
}

interface ReturnOptions extends BaseMutationOptions {
  itemId: string;
  warehouseId: string;
  quantity: number;
}

interface MutationResult {
